import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { AuthService } from '@src/services/apis/auth.service';
import { UsersService } from '@src/services/apis/users.service';

@Component({
	selector: 'app-root',
	templateUrl: './app.component.html',
	styleUrls: ['./app.component.scss'],
})
export class AppComponent {
	//
	// ---------
	// Constructor
	// ---------
	//

	auth: boolean = false;

	//
	// ---------
	// Constructor
	// ---------
	//
	constructor(private authService: AuthService, private usersService: UsersService) {}

	//
	// ---------
	// Form
	// ---------
	//

	loginForm = new FormGroup({
		username: new FormControl('', [Validators.required]),
		password: new FormControl('', [Validators.required, Validators.minLength(6)]),
	});

	//
	// ---------
	// OnInit
	// ---------
	//

	async ngOnInit() {
		await this.checkToken();
	}

	//
	// ---------
	// Event when user click on login button
	// ---------
	//

	onSubmit() {
		if (!this.loginForm.valid) return;

		this.usersService.getUserByName(this.loginForm.value.username!).subscribe({
			next: (res) => {
				if (res == null) {
					alert('Utilisateur ou mot de passe incorrect.');
					return;
				}

				this.userAuth();
			},

			error: (err) => {
				console.log(err);
			},

			complete: () => {},
		});

		console.log('this.loginForm.value');
	}

	//
	// ---------
	// Authenticated user after login
	// ---------
	//

	userAuth() {
		this.authService.login(this.loginForm.value).subscribe({
			next: (res) => {
				// save token in local storage
				localStorage.setItem('token', res.accessToken);
				console.log(res);
			},

			error: (err) => {
				alert('Utilisateur ou mot de passe incorrect.');
			},

			complete: () => {
				this.auth = true;
			},
		});
	}

	//
	// ---------
	// Check if user is authenticated
	// ---------
	//

	async checkToken() {
		this.authService.checkToken().subscribe({
			next: (res) => {
				console.log(res);
				this.auth = true;
			},

			error: (err) => {
				console.log(err);
			},

			complete: () => {},
		});
	}
}

